mapper.select:

select 

from ACADEMICCALENDAR
where ACADEMIC_YEAR = #{academicYear,jdbcType=VARCHAR}
  and ACADEMIC_TERM = #{academicTerm,jdbcType=VARCHAR}
  and ACADEMIC_SESSION = #{academicSession,jdbcType=VARCHAR}

mapper.delete:

delete from ACADEMICCALENDAR
where ACADEMIC_YEAR = #{academicYear,jdbcType=VARCHAR}
  and ACADEMIC_TERM = #{academicTerm,jdbcType=VARCHAR}
  and ACADEMIC_SESSION = #{academicSession,jdbcType=VARCHAR}
